"timeInterval": {

"start": "2024-10-02T21:00:00.000Z",

"end": "2024-10-03T21:00:00.000Z"

},

"searchQuery": {

"booleanQuery": {

"searchQuery": [

{

"equals": {

"eventItemKey": "deviceType",

"eventItemValue": "aW9z"

}

},

{

"range": {

"eventItemKey": "deviceRegistrationTimestamp",

"lowerBound": {

"eventItemValue": "MjAyNC0xMC0wMlQwMDowMDowMC4wMDBa",

"inclusive": true

},

